void ConsistentBreakfast()
{
	//int N = 1500; //power
	//int stDg = 22; //room temp
	//1 coffee
	cout << "N1 STARTED!" << endl;
	//a pour water into a kettle
	Sleep(5 * 1000);
	cout << "1.a done!" << endl;
	//b boil the kettle
	Sleep((10) * 1000);
	cout << "1.b done!" << endl;
	//c pour coffee into a cup
	Sleep(2 * 1000);
	cout << "1.c done!" << endl;
	//d fill a cupp with hot water
	Sleep(2 * 1000);
	cout << "1.d done!" << endl;
	cout << "N1 DONE!" << endl << endl;

	//2 omlette
	cout << "N2 STARTED!" << endl;
	//a preheat a pan
	Sleep(10 * 1000);
	cout << "2.a done!" << endl;
	//b break 2 eggs
	Sleep(30 * 1000);
	cout << "2.b done!" << endl;
	//c frying
	Sleep(10 * 1000);
	cout << "2.c done!" << endl;
	cout << "N2 DONE!" << endl << endl;

	//3 bacon
	cout << "N3 STARTED!" << endl;
	Sleep(10 * 1000);
	cout << "N3 DONE!" << endl << endl;

	//4 toasts
	cout << "N4 STARTED!" << endl;
	Sleep(10 * 1000);
	cout << "N4 DONE!" << endl << endl;

	//5 butter and jam
	cout << "N5 STARTED!" << endl;
	Sleep(10 * 1000);
	cout << "N5 DONE!" << endl << endl;

	//return?
}

